public class TextBasedRPG {

    static Scanner scanner = new Scanner(System.in);

    public static void main(String[] args) {
        System.out.println("Welcome to the Text-Based RPG!");

        // Load or create a new character
        Character player = loadOrCreateCharacter();

        // Main game loop
        while (true) {
            System.out.println("\nWhat would you like to do?");
            System.out.println("1. Fight an enemy");
            System.out.println("2. Save game");
            System.out.println("3. Quit");

            int choice = scanner.nextInt();
            switch (choice) {
                case 1:
                    // Fight an enemy
                    Enemy enemy = new Enemy("Goblin", 10, 2);
                    fight(player, enemy);
                    break;
                case 2:
                    // Save game
                    saveCharacter(player);
                    break;
                case 3:
                    // Quit
                    System.exit(0);
                    break;
                default:
                    System.out.println("Invalid option.");
            }
        }
    }

    private static Character loadOrCreateCharacter() {
        File file = new File("save.txt");
        if (file.exists()) {
            try {
                FileInputStream fis = new FileInputStream(file);
                ObjectInputStream ois = new ObjectInputStream(fis);
                Character character = (Character) ois.readObject();
                ois.close();
                return character;
            } catch (IOException | ClassNotFoundException e) {
                e.printStackTrace();
            }
        }

        System.out.print("Enter your character's name: ");
        String name = scanner.next();
        return new Character(name, 20, 5);
    }

    private static void saveCharacter(Character character) {
        try {
            FileOutputStream fos = new FileOutputStream("save.txt");
            ObjectOutputStream oos = new ObjectOutputStream(fos);
            oos.writeObject(character);
            oos.close();
            System.out.println("Game saved successfully!");
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private static void fight(Character player, Enemy enemy) {
        while (player.health > 0 && enemy.health > 0) {
            System.out.printf("\n%s attacks %s for %d damage!\n", player.name, enemy.name, player.attack);
            enemy.health -= player.attack;

            if (enemy.health <= 0) {
                System.out.printf("%s has been defeated!\n", enemy.name);
                break;
            }

            System.out.printf("%s attacks %s for %d damage!\n", enemy.name, player.name, enemy.attack);
            player.health -= enemy.attack;

            if (player.health <= 0) {
                System.out.printf("%s has been defeated! Game over.\n", player.name);
                System.exit(0);
            }
        }
    }
}

