class Transaction {
public:
    std::string sender;
    std::string receiver;
    double amount;

    Transaction(const std::string& sender, const std::string& receiver, double amount)
        : sender(sender), receiver(receiver), amount(amount) {}
};

class Block {
private:
    int index;
    time_t timestamp;
    std::vector<Transaction> transactions;
    std::string prevHash;
    std::string hash;
    int nonce;

    std::string calculateHash() const {
        std::stringstream ss;
        ss << index << timestamp << transactionString() << prevHash << nonce;
        return sha256(ss.str());
    }

    std::string transactionString() const {
        std::stringstream ss;
        for (const auto& tx : transactions) {
            ss << tx.sender << tx.receiver << tx.amount;
        }
        return ss.str();
    }

    static std::string sha256(const std::string& data) {
        unsigned char digest[SHA256_DIGEST_LENGTH];
        SHA256_CTX ctx;
        SHA256_Init(&ctx);
        SHA256_Update(&ctx, data.c_str(), data.size());
        SHA256_Final(digest, &ctx);

        std::stringstream ss;
        for (int i = 0; i < SHA256_DIGEST_LENGTH; ++i) {
            ss << std::hex << std::setw(2) << std::setfill('0') << (int)digest[i];
        }
        return ss.str();
    }

public:
    Block(int index, const std::vector<Transaction>& transactions, const std::string& prevHash)
        : index(index), timestamp(std::time(nullptr)), transactions(transactions),
          prevHash(prevHash), nonce(0) {
        hash = calculateHash();
    }

    void mineBlock(int difficulty) {
        std::string target(difficulty, '0');
        while (hash.substr(0, difficulty) != target) {
            ++nonce;
            hash = calculateHash();
        }
        std::cout << "Block mined: " << hash << std::endl;
    }

    int getIndex() const { return index; }
    time_t getTimestamp() const { return timestamp; }
    const std::vector<Transaction>& getTransactions() const { return transactions; }
    const std::string& getPrevHash() const { return prevHash; }
    const std::string& getHash() const { return hash; }
};

class Blockchain {
private:
    std::vector<Block> chain;
    int difficulty;

public:
    Blockchain(int difficulty) : difficulty(difficulty) {
        std::vector<Transaction> genesisTransactions;
        genesisTransactions.emplace_back("sender1", "receiver1", 50);
        chain.emplace_back(0, genesisTransactions, "0");
    }

    void addBlock(const std::vector<Transaction>& transactions) {
        chain.emplace_back(chain.size(), transactions, chain.back().getHash());
        chain.back().mineBlock(difficulty);
    }

    bool isValid() const {
        for (size_t i = 1; i < chain.size(); ++i) {
            const Block& currentBlock = chain[i];
            const Block& prevBlock = chain[i - 1];

            if (currentBlock.getHash() != currentBlock.calculateHash()) {
                return false;
            }

            if (currentBlock.getPrevHash() != prevBlock.getHash()) {
                return false;
            }
        }
        return true;
    }
};

int main() {
    Blockchain blockchain(4);

    std::vector<Transaction> transactions1;
    transactions1.emplace_back("sender2", "receiver2", 100);
    blockchain.addBlock(transactions1);

    std::vector<Transaction> transactions2;
    transactions2.emplace_back("sender3", "receiver3", 150);
    blockchain.addBlock(transactions2);

    if (blockchain.isValid()) {
        std::cout << "Blockchain is valid" << std::endl;
    } else {
        std::cout << "Blockchain is not valid" << std::endl;
    }

    return 0;
}

using System;

namespace GeometricShapes
{
    public abstract class Shape
    {
        public abstract double CalculateArea();
        public abstract double CalculatePerimeter();
    }

    public class Circle : Shape
    {
        public double Radius { get; set; }

        public Circle(double radius)
        {
            Radius = radius;
        }

        public override double CalculateArea()
        {
            return Math.PI * Math.Pow(Radius, 2);
        }

        public override double CalculatePerimeter()
        {
            return 2 * Math.PI * Radius;
        }
    }

    public class Rectangle : Shape
    {
        public double Length { get; set; }
        public double Width { get; set; }

        public Rectangle(double length, double width)
        {
            Length = length;
            Width = width;
        }

        public override double CalculateArea()
        {
            return Length * Width;
        }

        public override double CalculatePerimeter()
        {
            return 2 * (Length + Width);
        }
    }

    public class Triangle : Shape
    {
        public double Base { get; set; }
        public double Height { get; set; }
        public double SideA { get; set; }
        public double SideB { get; set; }

        public Triangle(double baseLength, double height, double sideA, double sideB)
        {
            Base = baseLength;
            Height = height;
            SideA = sideA;
            SideB = sideB;
        }

        public override double CalculateArea()
        {
            return 0.5 * Base * Height;
        }

        public override double CalculatePerimeter()
        {
            return Base + SideA + SideB;
        }
    }

    class Program
    {
        static void Main(string[] args)
        {
            Console.WriteLine("Enter the shape type (circle, rectangle, triangle):");
            string shapeType = Console.ReadLine().ToLower();

            Shape shape;

            switch (shapeType)
            {
                case "circle":
                    Console.Write("Enter the radius: ");
                    double radius = Convert.ToDouble(Console.ReadLine());
                    shape = new Circle(radius);
                    break;
                case "rectangle":
                    Console.Write("Enter the length: ");
                    double length = Convert.ToDouble(Console.ReadLine());
                    Console.Write("Enter the width: ");
                    double width = Convert.ToDouble(Console.ReadLine());
                    shape = new Rectangle(length, width);
                    break;
                case "triangle":
                    Console.Write("Enter the base: ");
                    double baseLength = Convert.ToDouble(Console.ReadLine());
                    Console.Write("Enter the height: ");
                    double height = Convert.ToDouble(Console.ReadLine());
                    Console.Write("Enter side A: ");
                    double sideA = Convert.ToDouble(Console.ReadLine());
                    Console.Write("Enter side B: ");
                    double sideB = Convert.ToDouble(Console.ReadLine());
                    shape = new Triangle(baseLength, height, sideA, sideB);
                    break;
                default:
                    Console.WriteLine("Invalid shape type.");
                    return;
            }

            Console.WriteLine($"Area: {shape.CalculateArea()}");
            Console.WriteLine($"Perimeter: {shape.CalculatePerimeter()}");

            Console.ReadKey();
        }
    }
}
