    {
        // Prompt the user to enter integers in ascending order.
        Console.WriteLine("Enter integers in ascending order, separated by spaces:");
        string input = Console.ReadLine();

        // Convert the input into an array of integers.
        int[] numbers = Array.ConvertAll(input.Split(' '), int.Parse);

        // Prompt the user to enter the number to be searched.
        Console.WriteLine("Enter the number to search for:");
        int target = int.Parse(Console.ReadLine());

        // Perform binary search on the array.
        int result = BinarySearch(numbers, target);

        // Print the position if the number is found, otherwise print a suitable message.
        if (result != -1)
            Console.WriteLine($"Number found at position: {result + 1}");
        else
            Console.WriteLine("Number not found.");
    }

    // Method to perform binary search.
    static int BinarySearch(int[] arr, int x)
    {
        int l = 0, r = arr.Length - 1;
        while (l <= r)
        {
            int m = l + (r - l) / 2;

            // Check if x is present at mid.
            if (arr[m] == x)
                return m;

            // If x greater, ignore left half.
            if (arr[m] < x)
                l = m + 1;

            // If x is smaller, ignore right half.
            else
                r = m - 1;
        }

        // Return -1 if we reach here (element is not present).
        return -1;
    }
}

