using json = nlohmann::json;

size_t WriteCallback(void* contents, size_t size, size_t nmemb, void* userp) {
    ((std::string*)userp)->append((char*)contents, size * nmemb);
    return size * nmemb;
}

std::string MakeRequest(const std::string& url, const std::string& token, const std::string& method, const json& data = {}) {
    CURL* curl;
    CURLcode res;
    std::string readBuffer;

    curl_global_init(CURL_GLOBAL_DEFAULT);
    curl = curl_easy_init();

    if (curl) {
        struct curl_slist* headers = nullptr;
        headers = curl_slist_append(headers, ("Authorization: token " + token).c_str());
        headers = curl_slist_append(headers, "Content-Type: application/json");

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_CUSTOMREQUEST, method.c_str());
        curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headers);

        if (!data.empty()) {
            curl_easy_setopt(curl, CURLOPT_POSTFIELDS, data.dump().c_str());
        }

        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, WriteCallback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, &readBuffer);

        res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            std::cerr << "curl_easy_perform() failed: " << curl_easy_strerror(res) << std::endl;
        }

        curl_easy_cleanup(curl);
        curl_slist_free_all(headers);
    }

    curl_global_cleanup();
    return readBuffer;
}

void CreateRepo(const std::string& token, const std::string& name) {
    json data = {{"name", name}};
    std::string response = MakeRequest("https://api.github.com/user/repos", token, "POST", data);
    json jsonResponse = json::parse(response);
    std::cout << "Created repository: " << jsonResponse["html_url"] << std::endl;
}

void ListRepos(const std::string& token) {
    std::string response = MakeRequest("https://api.github.com/user/repos", token, "GET");
    json jsonResponse = json::parse(response);

    for (const auto& repo : jsonResponse) {
        std::cout << repo["full_name"] << ": " << repo["html_url"] << std::endl;
    }
}

void DeleteRepo(const std::string& token, const std::string& owner, const std::string& repoName) {
    std::string url = "https://api.github.com/repos/" + owner + "/" + repoName;
    MakeRequest(url, token, "DELETE");
    std::cout << "Deleted repository: " << owner << "/" << repoName << std::endl;
}

