#include <iostream>
#include <vector>

using namespace std;

// Function to find the maximum value of a knapsack with given weight limit
int knapSack(int W, vector<int> wt, vector<int> val, int n) {
    // Create a 2D table for dynamic programming approach
    vector<vector<int>> K(n + 1, vector<int>(W + 1));

    // Build the table in bottom-up manner
    for (int i = 0; i <= n; i++) {
        for (int w = 0; w <= W; w++) {
            if (i == 0 || w == 0)
                K[i][w] = 0;
            else if (wt[i - 1] <= w)
                K[i][w] = max(val[i - 1] + K[i - 1][w - wt[i - 1]], K[i - 1][w]);
            else
                K[i][w] = K[i - 1][w];
        }
    }

    return K[n][W];
}

int main() {
    int n, W;
    cout << "Enter the number of items: ";
    cin >> n;

    vector<int> val(n), wt(n);

    cout << "Enter the values and weights of the items:" << endl;
    for (int i = 0; i < n; i++) {
        cin >> val[i] >> wt[i];
    }

    cout << "Enter the weight limit of the knapsack: ";
    cin >> W;

    cout << "The maximum value that can be obtained is: " << knapSack(W, wt, val, n) << endl;

    return 0;
}

