#include <iostream>
#include <unordered_map>

class TrieNode {
public:
    char value;
    bool isEndOfWord;
    std::unordered_map<char, TrieNode*> children;

    TrieNode(char ch) : value(ch), isEndOfWord(false) {}
};

class Trie {
private:
    TrieNode* root;

    void deleteHelper(TrieNode* node, const std::string& word, int index) {
        if (index == word.size()) {
            if (!node->isEndOfWord)
                return;
            node->isEndOfWord = false;
        } else {
            char ch = word[index];
            auto it = node->children.find(ch);
            if (it == node->children.end())
                return;
            deleteHelper(it->second, word, index + 1);
        }

        if (node->children.empty() && !node->isEndOfWord) {
            delete node;
            node = nullptr;
        }
    }

public:
    Trie() { root = new TrieNode('\0'); }

    void insert(const std::string& word) {
        TrieNode* currentNode = root;
        for (char ch : word) {
            if (currentNode->children.count(ch) == 0) {
                currentNode->children[ch] = new TrieNode(ch);
            }
            currentNode = currentNode->children[ch];
        }
        currentNode->isEndOfWord = true;
    }

    bool search(const std::string& word) {
        TrieNode* currentNode = root;
        for (char ch : word) {
            if (currentNode->children.count(ch) == 0) {
                return false;
            }
            currentNode = currentNode->children[ch];
        }
        return currentNode->isEndOfWord;
    }

    void remove(const std::string& word) {
        deleteHelper(root, word, 0);
    }
};

int main() {
    Trie trie;
    trie.insert("hello");
    trie.insert("world");

    std::cout << "Search 'hello': " << trie.search("hello") << std::endl; // Output: 1
    std::cout << "Search 'world': " << trie.search("world") << std::endl; // Output: 1

    trie.remove("hello");
    std::cout << "Search 'hello' after deletion: " << trie.search("hello") << std::endl; // Output: 0

    return 0;
}
