import re
import random
from nltk.tokenize import word_tokenize
from nltk.stem import PorterStemmer

class SimpleChatBot:
    def __init__(self):
        self.patterns = {
            "greetings": [
                r"hello",
                r"hi",
                r"hey",
                r"greetings"
            ],
            "goodbye": [
                r"bye",
                r"goodbye",
                r"see you later"
            ],
            "thanks": [
                r"thank you",
                r"thanks"
            ]
        }

        self.responses = {
            "greetings": [
                "Hello!",
                "Hi there!",
                "Hey!"
            ],
            "goodbye": [
                "Goodbye!",
                "See you later!",
                "Bye now!"
            ],
            "thanks": [
                "You're welcome!",
                "No problem!",
                "My pleasure!"
            ],
            "default": [
                "I'm not sure how to respond.",
                "Can you please rephrase that?"
            ]
        }

        self.stemmer = PorterStemmer()

    def preprocess(self, text):
        tokens = word_tokenize(text.lower())
        stemmed_tokens = [self.stemmer.stem(token) for token in tokens]
        return ' '.join(stemmed_tokens)

    def match_pattern(self, preprocessed_text):
        for category, patterns in self.patterns.items():
            for pattern in patterns:
                if re.search(pattern, preprocessed_text):
                    return category
        return None

    def generate_response(self, category):
        if category is None:
            return random.choice(self.responses["default"])
        else:
            return random.choice(self.responses[category])

    def chat(self, user_input):
        preprocessed_input = self.preprocess(user_input)
        matched_category = self.match_pattern(preprocessed_input)
        response = self.generate_response(matched_category)
        return response

if __name__ == "__main__":
    chatbot = SimpleChatBot()

    print("Welcome to the simple chatbot! (type 'quit' to exit)")
    while True:
        user_input = input("You: ")
        if user_input.lower() == "quit":
            break
        bot_response = chatbot.chat(user_input)
        print(f"Bot: {bot_response}")

