        BlobContainerClient blobContainerClient = new BlobContainerClientBuilder()
                .connectionString(CONNECTION_STRING)
                .containerName(CONTAINER_NAME)
                .buildClient();

        // Upload a file
        File fileToUpload = new File("path/to/your/local/file.txt");
        blobContainerClient.getBlobClient(fileToUpload.getName()).uploadFromFile(fileToUpload.getAbsolutePath());

        System.out.println("File uploaded: " + fileToUpload.getName());

        // List blobs in the container
        System.out.println("Listing blobs:");
        for (BlobItem blobItem : blobContainerClient.listBlobs()) {
            System.out.println(blobItem.getName());
        }

        // Download a file
        File downloadedFile = new File("path/to/save/downloaded/file.txt");
        try (FileOutputStream fos = new FileOutputStream(downloadedFile)) {
            blobContainerClient.getBlobClient(fileToUpload.getName()).download(fos);
        }
        System.out.println("File downloaded: " + downloadedFile.getAbsolutePath());

        // Delete a blob
        blobContainerClient.getBlobClient(fileToUpload.getName()).delete();
        System.out.println("File deleted: " + fileToUpload.getName());
    }
}
