```csharp
using System;
using System.Collections.Generic;
using System.Linq;

public class Student
{
    public string Name { get; set; }
    public int Age { get; set; }
    public double GPA { get; set; }
}

class Program
{
    static void Main(string[] args)
    {
        // Create a list of students
        List<Student> students = new List<Student>
        {
            new Student { Name = "John", Age = 18, GPA = 3.8 },
            new Student { Name = "Jane", Age = 20, GPA = 3.6 },
            new Student { Name = "Bob", Age = 19, GPA = 4.0 },
            new Student { Name = "Alice", Age = 21, GPA = 3.9 }
        };

        // Use LINQ to filter students who have GPA greater than or equal to 3.8
        var highGpaStudents = from s in students
                              where s.GPA >= 3.8
                              select s;

        Console.WriteLine("Students with GPA >= 3.8:");
        foreach (var student in highGpaStudents)
        {
            Console.WriteLine($"{student.Name} - GPA: {student.GPA}");
        }

        // Use LINQ to sort students by age
        var sortedStudents = from s in students
                             orderby s.Age
                             select s;

        Console.WriteLine("\nStudents sorted by age:");
        foreach (var student in sortedStudents)
        {
            Console.WriteLine($"{student.Name} - Age: {student.Age}");
        }

        // Use LINQ to transform students data into a new anonymous type
        var transformedData = from s in students
                              select new { FullName = s.Name, GradePointAverage = s.GPA };

        Console.WriteLine("\nTransformed student data:");
        foreach (var data in transformedData)
        {
            Console.WriteLine($"Full Name: {data.FullName}, Grade Point Average: {data.GradePointAverage}");
        }
    }
}
```

