class ShoppingCart {
  constructor() {
    this.cart = JSON.parse(localStorage.getItem("cart")) || [];
  }

  addItem(item, quantity) {
    const index = this.cart.findIndex((i) => i.item === item);

    if (index !== -1) {
      this.cart[index].quantity += quantity;
    } else {
      this.cart.push({ item, quantity });
    }

    this.saveCart();
  }

  removeItem(item) {
    this.cart = this.cart.filter((i) => i.item !== item);
    this.saveCart();
  }

  updateQuantity(item, newQuantity) {
    const index = this.cart.findIndex((i) => i.item === item);

    if (index !== -1) {
      this.cart[index].quantity = newQuantity;
      this.saveCart();
    }
  }

  getTotalCost(prices) {
    let totalCost = 0;

    for (const item of this.cart) {
      totalCost += item.quantity * prices[item.item];
    }

    return totalCost;
  }

  saveCart() {
    localStorage.setItem("cart", JSON.stringify(this.cart));
  }
}

