import java.sql.PreparedStatement;
import java.sql.SQLException;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class CsvToSqlite {

    public static void main(String[] args) {
        if (args.length != 3) {
            System.out.println("Usage: java CsvToSqlite <input_csv_file> <output_sqlite_db_file> <table_name>");
            return;
        }

        String inputCsvFile = args[0];
        String outputSqliteDbFile = args[1];
        String tableName = args[2];

        try {
            List<String[]> csvData = readCsv(inputCsvFile);
            storeInSqlite(csvData, outputSqliteDbFile, tableName);
        } catch (IOException | SQLException e) {
            System.err.println("Error: " + e.getMessage());
        }
    }

    private static List<String[]> readCsv(String filePath) throws IOException {
        List<String[]> data = new ArrayList<>();
        try (BufferedReader br = new BufferedReader(new FileReader(filePath))) {
            String line;
            while ((line = br.readLine()) != null) {
                data.add(line.split(","));
            }
        }
        return data;
    }

    private static void storeInSqlite(List<String[]> csvData, String dbFilePath, String tableName) throws SQLException {
        String url = "jdbc:sqlite:" + dbFilePath;

        try (Connection conn = DriverManager.getConnection(url)) {
            createTable(conn, tableName, csvData.get(0));
            insertRows(conn, tableName, csvData.subList(1, csvData.size()));
        }
    }

    private static void createTable(Connection conn, String tableName, String[] headerRow) throws SQLException {
        StringBuilder sb = new StringBuilder();
        sb.append("CREATE TABLE IF NOT EXISTS ").append(tableName).append(" (");

        for (int i = 0; i < headerRow.length; i++) {
            sb.append(headerRow[i]).append(" TEXT");
            if (i < headerRow.length - 1) {
                sb.append(", ");
            }
        }

        sb.append(");");

        try (PreparedStatement pstmt = conn.prepareStatement(sb.toString())) {
            pstmt.executeUpdate();
        }
    }

    private static void insertRows(Connection conn, String tableName, List<String[]> rows) throws SQLException {
        StringBuilder sb = new StringBuilder();
        sb.append("INSERT INTO ").append(tableName).append(" VALUES (");

        int columnCount = rows.get(0).length;
        for (int i = 0; i < columnCount; i++) {
            sb.append("?");
            if (i < columnCount - 1) {
                sb.append(", ");
            }
        }

        sb.append(");");

        try (PreparedStatement pstmt = conn.prepareStatement(sb.toString())) {
            for (String[] row : rows) {
                for (int i = 0; i < row.length; i++) {
                    pstmt.setString(i + 1, row[i]);
                }
                pstmt.addBatch();
            }
            pstmt.executeBatch();
        }
    }
}

