import java.util.*;

class Edge {
    int destination;
    int weight;

    public Edge(int destination, int weight) {
        this.destination = destination;
        this.weight = weight;
    }
}

public class DijkstraShortestPath {

    private static final int INFINITY = Integer.MAX_VALUE;

    public static void main(String[] args) {
        // Sample graph represented as an adjacency list
        List<List<Edge>> graph = new ArrayList<>();
        for (int i = 0; i < 6; i++) {
            graph.add(new ArrayList<>());
        }

        addEdge(graph, 0, 1, 4);
        addEdge(graph, 0, 2, 2);
        addEdge(graph, 1, 2, 5);
        addEdge(graph, 1, 3, 10);
        addEdge(graph, 2, 4, 3);
        addEdge(graph, 3, 4, 1);
        addEdge(graph, 3, 5, 6);
        addEdge(graph, 4, 5, 8);

        int source = 0;
        int destination = 5;

        List<Integer> shortestPath = dijkstra(graph, source, destination);

        System.out.println("Shortest path from node " + source + " to node " + destination + ":");
        for (Integer node : shortestPath) {
            System.out.print(node + " ");
        }
    }

    private static void addEdge(List<List<Edge>> graph, int src, int dest, int weight) {
        graph.get(src).add(new Edge(dest, weight));
        graph.get(dest).add(new Edge(src, weight)); // For undirected graph
    }

    private static List<Integer> dijkstra(List<List<Edge>> graph, int source, int destination) {
        int n = graph.size();
        int[] distances = new int[n];
        boolean[] visited = new boolean[n];
        int[] previous = new int[n];

        Arrays.fill(distances, INFINITY);
        Arrays.fill(previous, -1);

        distances[source] = 0;

        PriorityQueue<Integer> queue = new PriorityQueue<>(Comparator.comparingInt(node -> distances[node]));
        queue.add(source);

        while (!queue.isEmpty()) {
            int currentNode = queue.poll();

            if (visited[currentNode]) continue;
            visited[currentNode] = true;

            for (Edge edge : graph.get(currentNode)) {
                int neighbor = edge.destination;
                int weight = edge.weight;

                if (distances[currentNode] + weight < distances[neighbor]) {
                    distances[neighbor] = distances[currentNode] + weight;
                    previous[neighbor] = currentNode;
                    queue.add(neighbor);
                }
            }
        }

        List<Integer> path = new ArrayList<>();
        int current = destination;
        while (current != -1) {
            path.add(current);
            current = previous[current];
        }

        Collections.reverse(path);
        return path;
    }
}
