require('dotenv').config();
const Twit = require('twit');
const Sentiment = require('sentiment');

// Initialize Twitter API client and sentiment analyzer
const T = new Twit({
  consumer_key: process.env.TWITTER_API_KEY,
  consumer_secret: process.env.TWITTER_API_SECRET_KEY,
  access_token: process.env.TWITTER_ACCESS_TOKEN,
  access_token_secret: process.env.TWITTER_ACCESS_TOKEN_SECRET,
});

const sentiment = new Sentiment();

function analyzeTweets(keyword) {
  // Set search parameters for tweets containing the keyword
  const params = {
    q: keyword,
    count: 10,
    lang: 'en',
  };

  // Fetch tweets and perform sentiment analysis
  T.get('search/tweets', params, (err, data) => {
    if (err) {
      console.error(`Error fetching tweets: ${err}`);
      return;
    }

    const tweets = data.statuses.map((tweet) => tweet.text);
    let totalSentimentScore = 0;

    tweets.forEach((tweet) => {
      const result = sentiment.analyze(tweet);
      totalSentimentScore += result.score;
      console.log(`Tweet: ${tweet}\nSentiment score: ${result.score}\n`);
    });

    // Calculate and print average sentiment score
    const averageSentimentScore = totalSentimentScore / tweets.length;
    console.log(`Average sentiment score for keyword "${keyword}": ${averageSentimentScore}`);
  });
}

// Replace 'your_keyword' with the keyword you want to search for
analyzeTweets('your_keyword');
```
