  if (err) throw err;
  console.log('Connected to MySQL');
});

// Create users table if not exists
const createTableQuery = `
CREATE TABLE IF NOT EXISTS users (
  id INT AUTO_INCREMENT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  email VARCHAR(255) UNIQUE NOT NULL,
  phone_number VARCHAR(20)
);`;

connection.query(createTableQuery, (err, result) => {
  if (err) throw err;
  console.log('Users table created or already exists');
});

// Read and import CSV file
const filePath = path.join(__dirname, 'users.csv');

fs.createReadStream(filePath)
  .pipe(csvParser())
  .on('data', (row) => {
    // Validate data
    const { name, email, phone_number } = row;

    if (!name || !email || !phone_number) {
      console.error(`Invalid data: ${JSON.stringify(row)}`);
      return;
    }

    // Insert data into MySQL
    const insertUserQuery = `
      INSERT INTO users (name, email, phone_number)
      SELECT * FROM (SELECT ? AS name, ? AS email, ? AS phone_number) AS tmp
      WHERE NOT EXISTS (
        SELECT email FROM users WHERE email = ?
      ) LIMIT 1;
    `;

    connection.query(insertUserQuery, [name, email, phone_number, email], (err, result) => {
      if (err) throw err;
      if (result.affectedRows > 0) {
        console.log(`Inserted: ${JSON.stringify(row)}`);
      } else {
        console.log(`Skipped duplicate: ${email}`);
      }
    });
  })
  .on('end', () => {
    console.log('CSV file processed');
  });
```

