import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;
import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.sql.Statement;
import java.util.ArrayList;
import java.util.List;

public class UserDatabase {
    private static final String DB_URL = "jdbc:sqlite:user_database.db";

    public static void main(String[] args) {
        try {
            createTable();
            readCSVAndInsertData("user_data.csv");
            System.out.println("All Users:");
            List<User> allUsers = getAllUsers();
            printUserList(allUsers);

            System.out.println("\nUsers aged 25:");
            List<User> ageSpecificUsers = getUsersByAge(25);
            printUserList(ageSpecificUsers);

            System.out.println("\nUsers with email domain 'example.com':");
            List<User> domainSpecificUsers = getUsersByEmailDomain("example.com");
            printUserList(domainSpecificUsers);
        } catch (SQLException | IOException e) {
            e.printStackTrace();
        }
    }

    private static void createTable() throws SQLException {
        try (Connection conn = DriverManager.getConnection(DB_URL)) {
            Statement stmt = conn.createStatement();
            String sql = "CREATE TABLE IF NOT EXISTS users (" +
                    "id INTEGER PRIMARY KEY AUTOINCREMENT," +
                    "first_name TEXT NOT NULL," +
                    "last_name TEXT NOT NULL," +
                    "email TEXT NOT NULL UNIQUE," +
                    "age INTEGER NOT NULL)";
            stmt.execute(sql);
        }
    }

    private static void readCSVAndInsertData(String fileName) throws IOException, SQLException {
        try (BufferedReader br = new BufferedReader(new FileReader(fileName))) {
            String line;
            while ((line = br.readLine()) != null) {
                String[] data = line.split(",");
                if (data.length == 4) {
                    insertUser(data[0], data[1], data[2], Integer.parseInt(data[3]));
                }
            }
        }
    }

    private static void insertUser(String firstName, String lastName, String email, int age) throws SQLException {
        try (Connection conn = DriverManager.getConnection(DB_URL)) {
            String sql = "INSERT INTO users(first_name, last_name, email, age) VALUES(?, ?, ?, ?)";
            PreparedStatement pstmt = conn.prepareStatement(sql);
            pstmt.setString(1, firstName);
            pstmt.setString(2, lastName);
            pstmt.setString(3, email);
            pstmt.setInt(4, age);
            pstmt.executeUpdate();
        }
    }

    private static List<User> getAllUsers() throws SQLException {
        try (Connection conn = DriverManager.getConnection(DB_URL)) {
            Statement stmt = conn.createStatement();
            ResultSet rs = stmt.executeQuery("SELECT * FROM users");
            return getUsersFromResultSet(rs);
        }
    }

    private static List<User> getUsersByAge(int age) throws SQLException {
        try (Connection conn = DriverManager.getConnection(DB_URL)) {
            String sql = "SELECT * FROM users WHERE age = ?";
            PreparedStatement pstmt = conn.prepareStatement(sql);
            pstmt.setInt(1, age);
            ResultSet rs = pstmt.executeQuery();
            return getUsersFromResultSet(rs);
        }
    }

    private static List<User> getUsersByEmailDomain(String domain) throws SQLException {
        try (Connection conn = DriverManager.getConnection(DB_URL)) {
            String sql = "SELECT * FROM users WHERE email LIKE ?";
            PreparedStatement pstmt = conn.prepareStatement(sql);
            pstmt.setString(1, "%" + domain);
            ResultSet rs = pstmt.executeQuery();
            return getUsersFromResultSet(rs);
        }
    }

    private static List<User> getUsersFromResultSet(ResultSet rs) throws SQLException {
        List<User> users = new ArrayList<>();
        while (rs.next()) {
            int id = rs.getInt("id");
            String firstName = rs.getString("first_name");
            String lastName = rs.getString("last_name");
            String email = rs.getString("email");
            int age = rs.getInt("age");
            users.add(new User(id, firstName, lastName, email, age));
        }
        return users;
    }

    private static void printUserList(List<User> users) {
        for (User user : users) {
            System.out.println(user);
        }
    }

    static class User {
        int id;
        String firstName;
        String lastName;
        String email;
        int age;

        public User(int id, String firstName, String lastName, String email, int age) {
            this.id = id;
            this.firstName = firstName;
            this.lastName = lastName;
            this.email = email;
            this.age = age;
        }

        @Override
        public String toString() {
            return "User{" +
                    "id=" + id +
                    ", firstName='" + firstName + '\'' +
                    ", lastName='" + lastName + '\'' +
                    ", email='" + email + '\'' +
                    ", age=" + age +
                    '}';
        }
    }
}

