class Account {
private:
    int accountNumber;
    double balance;

public:
    Account(int accountNumber, double initialBalance)
        : accountNumber(accountNumber), balance(initialBalance) {}

    int getAccountNumber() const { return accountNumber; }

    double getBalance() const { return balance; }

    void deposit(double amount) { balance += amount; }

    bool withdraw(double amount) {
        if (balance >= amount) {
            balance -= amount;
            return true;
        } else {
            return false;
        }
    }

    bool transfer(Account& toAccount, double amount) {
        if (withdraw(amount)) {
            toAccount.deposit(amount);
            return true;
        } else {
            return false;
        }
    }
};

class Customer {
private:
    std::string name;
    int customerID;
    std::vector<Account> accounts;

public:
    Customer(const std::string& name, int customerID)
        : name(name), customerID(customerID) {}

    const std::string& getName() const { return name; }

    int getCustomerID() const { return customerID; }

    void addAccount(const Account& account) { accounts.push_back(account); }

    Account* findAccount(int accountNumber) {
        for (auto& account : accounts) {
            if (account.getAccountNumber() == accountNumber) {
                return &account;
            }
        }
        return nullptr;
    }
};

class Transaction {
public:
    static void deposit(Customer& customer, int accountNumber, double amount) {
        Account* account = customer.findAccount(accountNumber);
        if (account != nullptr) {
            account->deposit(amount);
            std::cout << "Deposited $" << amount << " into account #" << accountNumber << ". New balance: $" << account->getBalance() << std::endl;
        } else {
            std::cout << "Account not found." << std::endl;
        }
    }

    static void withdraw(Customer& customer, int accountNumber, double amount) {
        Account* account = customer.findAccount(accountNumber);
        if (account != nullptr) {
            if (account->withdraw(amount)) {
                std::cout << "Withdrew $" << amount << " from account #" << accountNumber << ". New balance: $" << account->getBalance() << std::endl;
            } else {
                std::cout << "Insufficient funds." << std::endl;
            }
        } else {
            std::cout << "Account not found." << std::endl;
        }
    }

    static void transfer(Customer& fromCustomer, int fromAccountNumber, Customer& toCustomer, int toAccountNumber, double amount) {
        Account* fromAccount = fromCustomer.findAccount(fromAccountNumber);
        Account* toAccount = toCustomer.findAccount(toAccountNumber);

        if (fromAccount != nullptr && toAccount != nullptr) {
            if (fromAccount->transfer(*toAccount, amount)) {
                std::cout << "Transferred $" << amount << " from account #" << fromAccountNumber << " to account #" << toAccountNumber << "." << std::endl;
            } else {
                std::cout << "Insufficient funds." << std::endl;
            }
        } else {
            std::cout << "One or both accounts not found." << std::endl;
        }
    }
};

