    // Create customers
    Customer alice("Alice", 1);
    Customer bob("Bob", 2);

    // Create accounts for Alice and Bob
    Account alicesAccount(1001, 500.0);
    Account bobsAccount(1002, 300.0);

    // Add accounts to customers
