	return false
}

func isExcludedFile(file string) bool {
	ext := filepath.Ext(file)
	for _, excluded := range excludedFiles {
		if ext == excluded {
			return true
		}
	}
	return false
}

func walkFunc(path string, info os.FileInfo, err error) error {
	if err != nil {
		fmt.Printf("Error accessing path %q: %v\n", path, err)
		return err
	}

	if !info.IsDir() && !isExcludedFile(info.Name()) {
		parentDir := filepath.Base(filepath.Dir(path))
		if !isExcludedDir(parentDir) {
			totalSize += info.Size()
		}
	}

	return nil
}

func main() {
	err := filepath.Walk(path, walkFunc)
	if err != nil {
		fmt.Printf("Error walking the path %q: %v\n", path, err)
		os.Exit(1)
	}

	const (
		B  = 1.0
		KB = B * 1024
		MB = KB * 1024
		GB = MB * 1024
	)

	var sizeStr string
	switch {
	case totalSize >= GB:
		sizeStr = fmt.Sprintf("%.2f GB", float64(totalSize)/GB)
	case totalSize >= MB:
		sizeStr = fmt.Sprintf("%.2f MB", float64(totalSize)/MB)
	case totalSize >= KB:
		sizeStr = fmt.Sprintf("%.2f KB", float64(totalSize)/KB)
	default:
		sizeStr = fmt.Sprintf("%d bytes", totalSize)
	}

	fmt.Printf("Total size of files in directory: %s\n", sizeStr)
}
